const fs = require('fs');

  // Get all items that came through the false branch
  const items = $input.all();
  const results = [];

  for (let i = 0; i < items.length; i++) {
    // Get the corresponding item from Load Companies and Combine node
    const loadCompaniesItem = $('Load Companies and Combine').all()[i];

    const grantData = loadCompaniesItem.json.grant;
    const companyData = loadCompaniesItem.json.company;

    const title = grantData.Title;
    const link = grantData.Link;
    const date = grantData.Date;
    const description = grantData.Description;
    const companyId = companyData.id;
    const companyName = companyData.name;

    // This node is on FALSE branch, so relevant is "no"
    const relevant = "no";

    // Escape double quotes in description
    const escapedDescription = description.replace(/"/g, '""');
    const csvRow = `"${title}","${link}","${date}","${escapedDescription}","${relevant}"\n`;

    // ‚≠ê CHANGE: Save to company-specific CSV file
    const csvPath = `C:\\Users\\Korisnik\\Desktop\\Alicorn\\n8n_eu_grants\\data\\processed\\non_relevant_${companyId}.csv`;

    // Check if file exists, if not create with header
    if (!fs.existsSync(csvPath)) {
      const header = '"Title","Link","Date","Description","Relevant"\n';
      fs.writeFileSync(csvPath, header);
    }

    // Append new row to CSV
    fs.appendFileSync(csvPath, csvRow);

    results.push({
      json: {
        message: `Grant "${title}" marked as not relevant for ${companyName}`,
        title: title,
        link: link,
        date: date,
        company_id: companyId,
        company_name: companyName,
        relevant: relevant,
        saved_to: `non_relevant_${companyId}.csv`
      }
    });
  }

  console.log(`Saved ${results.length} non-relevant grants to company-specific CSV files`);

  return results;

