// Read companies.json file
 const fs = require('fs');
 const companiesPath = 'C:\\\\Users\\\\Korisnik\\\\Desktop\\\\Alicorn\\\\n8n_eu_grants\\\\data\\\\companies.json';

 // Programme name mapping: companies.json format ‚Üí scraper Source format
 const programmeMapping = {
   'Horizon Europe': 'horizon_europe',
   'Digital Europe': 'digital_europe',
   'Creative Europe': 'creative_europe',
   'Erasmus+': 'erasmus',
   'Interreg': 'adrion',
   'Danube Region Programme': 'danube',
   'Danube Programme': 'danube',
   'Central Europe Program': 'central_europe',
   'Central Europe Programme': 'central_europe',
   'COSME': 'cosme',
   'LIFE': 'life'
 };

 let companies;
 try {
   const fileContent = fs.readFileSync(companiesPath, 'utf8');
   companies = JSON.parse(fileContent);
 } catch (error) {
   console.error('Error reading companies.json:', error);
   return [];
 }

 // Get all grants from previous node
 const grants = $input.all();

 // Create combinations: each grant √ó each ELIGIBLE company
 const output = [];
 let skippedCount = 0;

 for (const grantItem of grants) {
   const grantSource = grantItem.json.Source; // e.g., "horizon_europe"

   for (const company of companies) {
     // Get company's eligible programmes and convert to scraper format
     const eligibleSources = (company.profile.eligible_programs || [])
       .map(prog => programmeMapping[prog])
       .filter(Boolean);

     // Check if this grant's source matches company's eligible programmes
     if (!eligibleSources.includes(grantSource)) {
       skippedCount++;
       continue; // Skip - company not eligible for this programme
     }

     output.push({
       json: {
         // Grant data
         grant: {
           Title: grantItem.json.Title,
           Link: grantItem.json.Link,
           Date: grantItem.json.Date,
           Description: grantItem.json.Description,
           Source: grantSource
         },
         // Company data
         company: {
           id: company.id,
           name: company.name,
           slack_channel_id: company.slack_channel_id,
           slack_user: company.slack_user,
           email: company.email_DISABLED,
           profile: company.profile
         }
       }
     });
   }
 }

 console.log(`Created ${output.length} grant-company combinations`);
 console.log(`Skipped ${skippedCount} combinations (programme not eligible)`);
 console.log(`Pre-filtering saved ${skippedCount} LLM calls!`);

 return output;

// Parse the AI response and add company + grant context
 const items = $input.all();
 const results = [];

 // Get ALL items from Load_Companies_And_Combine
 const allCombinations = $('Load Companies and Combine').all();

 // Get the indices of items that went to true branch from If node
 // We need to match based on the "Is it for me?" output containing "Yes"
 const isItForMeOutputs = $('Is it for me?').all();

 // Find which indices passed the If condition (contain "Yes")
 const trueIndices = [];
 for (let i = 0; i < isItForMeOutputs.length; i++) {
   const response = isItForMeOutputs[i].json.output?.[0]?.content?.[0]?.text || '';
   if (response.toLowerCase().includes('yes')) {
     trueIndices.push(i);
   }
 }

 console.log(`Found ${trueIndices.length} items that passed relevance check`);
 console.log(`Processing ${items.length} items from Extract Information`);

 for (let i = 0; i < items.length; i++) {
   // Get the AI response text from Extract Information node
   let aiResponse = items[i].json.output?.[0]?.content?.[0]?.text || '{}';

   // Try to parse JSON from the response
   let extracted = {};
   try {
     // Remove markdown code blocks if present
     aiResponse = aiResponse.replace(/```json\n?/g, '').replace(/```\n?/g, '');

     // Find JSON in the response
     const jsonMatch = aiResponse.match(/\{[\s\S]*\}/);
     if (jsonMatch) {
       extracted = JSON.parse(jsonMatch[0]);
     }
   } catch (e) {
     console.error('Failed to parse AI response as JSON:', e);
     extracted = {
       summary: 'Failed to extract',
       total_funding_available: 'Not specified',
       application_type: 'Not specified',
       partner_types: [],
       partner_origins: [],
       co_financing_required: 'Not specified'
     };
   }

   // Get the original index from Load_Companies_And_Combine
   const originalIndex = trueIndices[i];
   const originalItem = allCombinations[originalIndex];

   const companyData = originalItem?.json?.company;
   const grantData = originalItem?.json?.grant;

   results.push({
     json: {
       // Extracted grant details
       summary: extracted.summary || 'Not specified',
       total_funding_available: extracted.total_funding_available || 'Not specified',
       application_type: extracted.application_type || 'Not specified',
       partner_types: Array.isArray(extracted.partner_types) ? extracted.partner_types.join(', ') : (extracted.partner_types || 'Not specified'),
       partner_origins: Array.isArray(extracted.partner_origins) ? extracted.partner_origins.join(', ') : (extracted.partner_origins || 'Not specified'),       
       co_financing_required: extracted.co_financing_required || 'Not specified',

       // Grant info from the grant object
       title: grantData?.Title || 'No title',
       link: grantData?.Link || 'No link',
       date: grantData?.Date || 'No date',
       source: grantData?.Source || 'Unknown',

       // Company info for Slack routing
       company_name: companyData?.name || 'Unknown',
       company_id: companyData?.id || 'Unknown',
       slack_channel_id: companyData?.slack_channel_id || '',

       // Relevance info
       relevant: 'Yes'
     }
   });
 }

 console.log(`Processed ${results.length} relevant grants`);

 return results;

// Aggregate grants by company for daily digest
  const items = $input.all();

  // Group grants by company_id
  const companiesMap = {};

  for (const item of items) {
    const companyId = item.json.company_id;
    const companyName = item.json.company_name;
    const slackChannelId = item.json.slack_channel_id;

    if (!companiesMap[companyId]) {
      companiesMap[companyId] = {
        company_id: companyId,
        company_name: companyName,
        slack_channel_id: slackChannelId,
        grants: []
      };
    }

    // Add this grant to the company's list
    companiesMap[companyId].grants.push({
      title: item.json.title,
      link: item.json.link,
      date: item.json.date,
      source: item.json.source,
      summary: item.json.summary,
      total_funding_available: item.json.total_funding_available,
      application_type: item.json.application_type,
      partner_types: item.json.partner_types,
      partner_origins: item.json.partner_origins,
      co_financing_required: item.json.co_financing_required
    });
  }

  // Convert to array - one item per company
  const output = Object.values(companiesMap).map(company => ({
    json: {
      company_id: company.company_id,
      company_name: company.company_name,
      slack_channel_id: company.slack_channel_id,
      grant_count: company.grants.length,
      grants: company.grants
    }
  }));

  console.log(`Aggregated ${items.length} grant-company matches into ${output.length} company digests`);

  return output;

