// Get companies that have grants (from Aggregate By Company)
  const companiesWithGrants = $input.all();

  // Load all companies from JSON
  const fs = require('fs');
  const companiesPath = 'C:\\Users\\Korisnik\\Desktop\\Alicorn\\n8n_eu_grants\\data\\companies.json';
  const allCompanies = JSON.parse(fs.readFileSync(companiesPath, 'utf8'));

  // Create a map of companies that already have grants
  const companiesWithGrantsMap = new Map();
  for (const item of companiesWithGrants) {
    companiesWithGrantsMap.set(item.json.company_id, item);
  }

  console.log(`${companiesWithGrants.length} companies have grants`);

  // Build output: include companies with grants + add companies with 0 grants
  const output = [];

  for (const company of allCompanies) {
    if (companiesWithGrantsMap.has(company.id)) {
      // Company has grants - keep existing item
      output.push(companiesWithGrantsMap.get(company.id));
    } else {
      // Company has 0 grants - create "no grants" item
      output.push({
        json: {
          company_id: company.id,
          company_name: company.name,
          slack_channel_id: company.slack_channel_id,
          grant_count: 0,
          grants: []
        }
      });
    }
  }

  console.log(`Total companies in output: ${output.length}`);
  console.log(`Companies with 0 grants: ${output.filter(o => o.json.grant_count === 0).length}`);

  return output;
